import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';

class NameForm extends React.Component {
    constructor(props) {
        super();
        this.state = {
            nome: "",
            sexo: ""
        };
        this.onChange = (evento) => {
            const state = object.assign({}, this.state);
        };
    }


    render() {
      return (
        <form onSubmit={this.handleSubmit}>
          <label>
            Nome: <input type="text" value={this.state.value} onChange={this.handleChange} />
          </label>
            sexo: <input type="radio" name="sexo" value="feminino" onChange={this.onChange} checked={this.state.sexo==="masculino"} />

          <input type="submit" value="Enviar" />
        </form>
      );
    }
  }

ReactDOM.render(<NameForm />, document.getElementById('root'));
